import React, { useState } from 'react';

const menubackground = {
  width: '25%',
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'flex-start',
  alignItems: 'left',
  border: '2px solid #ffffff',
  backgroundColor: '#ffffff',
  fontFamily: "'Concert One', cursive",
  fontSize: '1.2rem',
  boxShadow: '0px 0px 15px 5px rgba(255, 255, 255, 0.6)',
  border: 'none',
  position: 'fixed',
  top: '0',
  left: '0',
  zIndex: '1000',
  paddingTop: '50px',
};

const backButtonStyle = {
  marginTop: '10px',
  background: '#007bff',
  color: '#fff',
  border: 'none',
  padding: '10px 20px',
  borderRadius: '5px',
  cursor: 'pointer',
  fontSize: '16px',
  textAlign: 'center',
  width: 'auto',
  display: 'inline-block',
  transition: 'background 0.3s ease',
};

const sliderStyle = {
  width: '80%',
  margin: '10px 10%',
};

const Menu = ({ handleBack, handleVolumeChange, handlePlaybackRateChange, handleEffects }) => {
  const [volume, setVolume] = useState(1);
  const [playbackRate, setPlaybackRate] = useState(1);
  const [reverb, setReverb] = useState(0); // Example effect

  const onVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    handleVolumeChange(newVolume); // Update parent component
  };

  const onPlaybackRateChange = (e) => {
    const newRate = parseFloat(e.target.value);
    setPlaybackRate(newRate);
    handlePlaybackRateChange(newRate); // Update playback rate
  };

  const onReverbChange = (e) => {
    const newReverb = parseFloat(e.target.value);
    setReverb(newReverb);
    handleEffects('reverb', newReverb); // Apply reverb effect
  };

  return (
    <div style={menubackground}>
      <h1>Audio Editing Menu</h1>
      <label>Volume</label>
      <input
        style={sliderStyle}
        type="range"
        min="0"
        max="1"
        step="0.01"
        value={volume}
        onChange={onVolumeChange}
      />
      
      <label>Playback Speed</label>
      <input
        style={sliderStyle}
        type="range"
        min="0.5"
        max="2"
        step="0.1"
        value={playbackRate}
        onChange={onPlaybackRateChange}
      />
      
      <label>Reverb</label>
      <input
        style={sliderStyle}
        type="range"
        min="0"
        max="1"
        step="0.1"
        value={reverb}
        onChange={onReverbChange}
      />
      
      <button style={backButtonStyle} onClick={handleBack}>Back</button>
    </div>
  );
};

export default Menu;
