import React, { useState } from 'react';
import menubutton from '../images/icons/menubutton.png';
import sparkless from '../images/icons/drawstar.png';
import Menu from './Menu.js';
import AIMenu from './Ai.js';

const sidebarContainerStyle = {
    width: '25%',
    height: '100%',
    display: 'flex',
    justifyContent: 'left',
    alignItems: 'center', 
    flexDirection: 'column',  
    border: '2px solid #ffffff',
    backgroundColor: 'rgba(255, 255, 255, 0.3)',  
    fontFamily: "'Concert One', cursive",
    textAlign: 'left',
    fontSize: '1.2rem',
    boxShadow: '0px 0px 15px 5px rgba(255, 255, 255, 0.6)',
    position: 'fixed',
    top: '0',
    left: '0',
    zIndex: '1001',
    paddingTop: '5%',
    transition: 'width 0.3s ease',
};

const expandedSidebarStyle = {
  ...sidebarContainerStyle,
  width: '25%', 
};

const collapsedSidebarStyle = {
  ...sidebarContainerStyle,
  width: '0%',
};

// Button base style
const buttonStyle = {
  backgroundColor: '#58336a',  // Base background color
  color: '#fff',               // White text
  border: 'none',              // Clean look
  padding: '12px 24px',        // Ample padding
  borderRadius: '8px',         // Rounded corners
  cursor: 'pointer',           // Pointer cursor for interactivity
  fontSize: '16px',            // Standard font size
  textAlign: 'center',         // Center the text
  display: 'flex',             // Flex for inner alignment
  alignItems: 'center',        // Vertical alignment of content
  justifyContent: 'center',    // Horizontal alignment of content
  transition: 'all 0.3s ease', // Smooth hover/active transitions
  margin: '10px 0',            // Spacing between buttons
  width: '100%',               // Full width
};

// Heading style remains the same
const headingStyle = {
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'center',
  fontSize: '1.5rem',
  color: '#ffffff', // Adjusted to match button text color
  fontFamily: 'Concert One',
  margin: '0 0 0 10px',
  padding: 0,
  height: 'auto',
};

// Hover effect
const buttonHoverStyle = {
  backgroundColor: '#7c4d91',
  transform: 'scale(1.05)',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
};

// Active state
const buttonActiveStyle = {
  transform: 'scale(0.98)',
  boxShadow: 'none',
};

// Focus effect
const buttonFocusStyle = {
  outline: 'none',
  boxShadow: '0 0 0 3px rgba(88, 51, 106, 0.5)',
};

const menubuttonStyle = {
    display: 'flex',
    flexDirection: 'row',  
    alignItems: 'center',
    justifyContent: 'center',
    position: 'fixed',
    top: '1%',  
    left: '0.5%',
    zIndex: '1001',  
    cursor: 'pointer',
    backgroundColor: 'rgba(255, 255, 255, 0.0)',  
    border: 'none',
    padding: '0',
    width: 'auto',
    height: 'auto',
};




function Sidebar({ waveData, fileLoaded }) {
  const [isMenuVisible, setMenuVisible] = useState(null);
  const [isFileWarningVisible, setFileWarningVisible] = useState(false);
  const [isSidebarExpanded, setSidebarExpanded] = useState(true);

  const toggleMenu = (menu) => {
    if (menu === 'mixerMenu' && !fileLoaded) {
      // Show warning and prevent menu from opening
      setFileWarningVisible(true);
    } else {
      // Open the menu if conditions are met
      setMenuVisible(isMenuVisible === menu ? null : menu);
      setFileWarningVisible(false);
    }
  };

  const handleBack = () => {
    setMenuVisible(null);
    setFileWarningVisible(false);
  };

  const toggleSidebar = () => {
    setSidebarExpanded(!isSidebarExpanded);
  };
  const handleMouseEnter = (e) => (e.target.style = { ...buttonStyle, ...buttonHoverStyle });
  const handleMouseLeave = (e) => (e.target.style = buttonStyle);
  const handleMouseDown = (e) => (e.target.style = { ...buttonStyle, ...buttonActiveStyle });
  const handleMouseUp = (e) => (e.target.style = buttonStyle);
  const handleFocus = (e) => (e.target.style = { ...buttonStyle, ...buttonFocusStyle });
  const handleBlur = (e) => (e.target.style = buttonStyle);
  return (
    <div style={isSidebarExpanded ? expandedSidebarStyle : collapsedSidebarStyle}>
      <button style={menubuttonStyle} onClick={() => toggleSidebar()}>
        <img src={menubutton} alt="Menu Button" style={{ width: '33px', height: 'auto' }} />
        <h1 style={headingStyle}>MELLOWDIES</h1>
      </button>

      {isSidebarExpanded && (
        <>
          {isMenuVisible === null && (
            <>
            <button
              style={buttonStyle}
              onClick={() => toggleMenu('aiSuggestionMenu')}
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
              onMouseDown={handleMouseDown}
              onMouseUp={handleMouseUp}
              onFocus={handleFocus}
              onBlur={handleBlur}
            >
              <h1 style={headingStyle}>AI EDITOR</h1>
            </button>
        
            <button
              style={buttonStyle}
              onClick={() => toggleMenu('mixerMenu')}
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
              onMouseDown={handleMouseDown}
              onMouseUp={handleMouseUp}
              onFocus={handleFocus}
              onBlur={handleBlur}
            >
              <h1 style={headingStyle}>MIXER</h1>
            </button>
          </>
          )}
          {isMenuVisible === 'mainMenu' && <Menu />}
          {isMenuVisible === 'aiSuggestionMenu' && (
            <AIMenu handleBack={handleBack} waveData={waveData} />
          )}
          {isMenuVisible === 'mixerMenu' && <Menu handleBack={handleBack} waveData={waveData} />}
          {isFileWarningVisible && (
            <div
              style={{
                color: 'red',
                textAlign: 'center',
                marginTop: '10px',
                fontSize: '1rem',
                backgroundColor: 'rgba(255, 0, 0, 0.1)',
                padding: '10px',
                borderRadius: '8px',
              }}
            >
              <p>Warning: Please load a file before accessing the Mixer!</p>
              <button
                style={{
                  marginTop: '5px',
                  padding: '5px 10px',
                  borderRadius: '4px',
                  border: 'none',
                  backgroundColor: 'red',
                  color: 'white',
                  cursor: 'pointer',
                }}
                onClick={() => setFileWarningVisible(false)}
              >
                Dismiss
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default Sidebar;
