import React, { useRef, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';  
import WaveSurfer from 'wavesurfer.js';
import WebAudioPlayer from 'wavesurfer.js/dist/webaudio.js';
import RegionsPlugin from "wavesurfer.js/dist/plugins/regions.js";
import TimelinePlugin from 'wavesurfer.js/dist/plugins/timeline';  
import { useLocation } from 'react-router-dom';
import Sidebar from './Sidebar.js';
import PlayButton from './PlayButton.js';
//import PlusIcon from '../images/icons/plus.png';
import './Landingpage.css';  

var blobber = require('audiobuffer-to-blob');
const waveSurferData = [];
const regions = RegionsPlugin.create();
const audioContext = new AudioContext();
const webAudioPlayer = new WebAudioPlayer(audioContext);
let fileLoaded = false;
const eqBands = [32, 64, 125, 250, 500, 1000, 2000, 4000, 8000, 16000];
let filters = [];

function createFilters (audioCtx, band) {
    const filter = audioCtx.createBiquadFilter()
    filter.type = band <= 32 ? 'lowshelf' : band >= 16000 ? 'highshelf' : 'peaking'
    filter.gain.value = 0
    filter.Q.value = 1 
    filter.frequency.value = band 
    return filter
  }

for (let i = 0; i < 10; i++) {
    let filter = createFilters(webAudioPlayer.audioContext, eqBands[i])
    filters.push(filter);
}

function Landingpage() {
    const navigate = useNavigate();
    const wavesurferRefs = useRef([]);
    const containerRefs = useRef([]);
    const musicBackgroundRef = useRef(null);
    const location = useLocation();

    const [audioFiles, setAudioFiles] = useState(location.state?.audioFiles || []);
    const [isReady, setIsReady] = useState(false);
    const [speed, setSpeed] = useState(1);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const fileInputRef = useRef(null);
    let longestDuration = 0;

    useEffect(() => {
        if (location.state?.audioFiles) {
            setAudioFiles(location.state.audioFiles);
        }
    }, [location.state]);

    const initializeWaveSurfer = () => {
        wavesurferRefs.current = [];

        audioFiles.forEach((file, index) => {
            const container = containerRefs.current[index];
            if (!container) {
                console.warn(`Missing container for track at index ${index}`);
                return;
            }

            const waveSurfer = WaveSurfer.create({
                container,
                waveColor: '#1E90FF',
                progressColor: '#32CD32',
                height: 80,
                cursorColor: '#FF4500',
                backgroundColor: 'transparent',
                minPxPerSec: 50,
                plugins: [RegionsPlugin.create()],
            });

            waveSurfer.on('ready', () => {
                setIsReady(true);
                const duration = waveSurfer.getDuration();
                longestDuration = Math.max(longestDuration, duration);
            });

            waveSurfer.on('region-created', (region) => {
                console.log(`Region created: ${region.id}`);
            });

            waveSurfer.load(file.url);
            wavesurferRefs.current[index] = waveSurfer;
        });
    };

    const delayedWaveSurferInit = () => {
        setTimeout(initializeWaveSurfer, 100);
    };

    if (audioFiles.length > 0 && wavesurferRefs.current.length === 0) {
        delayedWaveSurferInit();
    }

    const handleFileAddition = (event) => {
        const addedFiles = Array.from(event.target.files).map((file) => ({
            name: file.name,
            url: URL.createObjectURL(file),
            mimeType: file.type,
        }));

        if (addedFiles.length > 0) {
            setAudioFiles((prev) => [...prev, ...addedFiles]);
        } else {
            alert('No audio files selected.');
        }
    };

    const toggleDropdown = () => {
        setIsDropdownOpen((prev) => !prev);
    };

    return (
        <div className="pageBackground">
            <div ref={musicBackgroundRef} className="musicBackground">
                {audioFiles.map((file, index) => (
                    <div key={index}>
                        <div className="trackNameStyle">{file.name}</div>
                        <div className="timelineStyle"></div>
                        <div className="waveformStyle" ref={(el) => (containerRefs.current[index] = el)}></div>
                    </div>
                ))}
            </div>

            <button className="fileButton" onClick={() => fileInputRef.current.click()}>
                Add Files
            </button>
            <input
                type="file"
                multiple
                accept="audio/*"
                ref={fileInputRef}
                style={{ display: 'none' }}
                onChange={handleFileAddition}
            />

            <button className="dropdownToggle" onClick={toggleDropdown}>
                {isDropdownOpen ? 'Close Dropdown' : 'Open Dropdown'}
            </button>
        </div>
    );
}



export default Landingpage;
