import React, { useState, useRef, useEffect } from 'react';
import WaveSurfer from 'wavesurfer.js';
import { useLocation } from 'react-router-dom';
import Sidebar from './Sidebar'; // Assuming Sidebar is the menu component
import PlayButton from './PlayButton'; // Assuming PlayButton contains control buttons

function Landingpage() {
    const wavesurferRefs = useRef([]);  // Array to hold refs for each WaveSurfer instance
    const audioContextRefs = useRef([]); // Array to hold AudioContext refs
    const filterNodeRefs = useRef([]); // Array to hold filter node refs
    const location = useLocation();
    const { audioFiles } = location.state || { audioFiles: [] };
    const [isReady, setIsReady] = useState(false);
    const [speed, setSpeed] = useState(1);
    const [progress, setProgress] = useState(0);
    const [filterFrequency, setFilterFrequency] = useState(22050); // Default to max frequency for no filter

    useEffect(() => {
        // Initialize the wavesurferRefs and audioContextRefs arrays
        wavesurferRefs.current = [];
        audioContextRefs.current = [];
        filterNodeRefs.current = [];

        if (audioFiles && audioFiles.length > 0) {
            audioFiles.forEach((file, index) => {
                const containerId = `waveform-${index}`;

                const audioContext = new (window.AudioContext || window.webkitAudioContext)();
                const filterNode = audioContext.createBiquadFilter();
                filterNode.type = 'lowpass';
                filterNode.frequency.value = filterFrequency;

                const waveSurfer = WaveSurfer.create({
                    container: `#${containerId}`,
                    waveColor: 'blue',
                    progressColor: '#00FFFF',
                    height: 75,
                    autoCenter: true,
                    interact: true,
                    backend: 'MediaElement',
                    cursorWidth: 2,
                    cursorColor: '#FF0000',
                    backgroundColor: 'rgba(255, 255, 255, 0)',
                    minPxPerSec: 100,
                });

                waveSurfer.load(file.url);
                
                // Connecting Web Audio API nodes
                const mediaElementSource = audioContext.createMediaElementSource(waveSurfer.backend.media);
                mediaElementSource.connect(filterNode);
                filterNode.connect(audioContext.destination); // Connect filter to the destination

                waveSurfer.on('ready', () => {
                    setIsReady(true);
                });

                waveSurfer.on('audioprocess', () => {
                    setProgress((waveSurfer.getCurrentTime() / waveSurfer.getDuration()) * 100);
                });

                waveSurfer.on('seek', (progress) => {
                    setProgress(progress * 100);
                });

                // Store the waveSurfer, AudioContext, and filter nodes in their respective refs
                wavesurferRefs.current[index] = waveSurfer;
                audioContextRefs.current[index] = audioContext;
                filterNodeRefs.current[index] = filterNode;
            });

            // Cleanup function to properly handle the destruction of WaveSurfer instances and AudioContext
            return () => {
                wavesurferRefs.current.forEach(waveSurfer => {
                    if (waveSurfer) {
                        waveSurfer.destroy();
                    }
                });
                audioContextRefs.current.forEach(audioContext => {
                    if (audioContext) {
                        audioContext.close();
                    }
                });
            };
        } else {
            console.log('No audio files available to display.');
        }
    }, [audioFiles, filterFrequency]);

    const applyFilterToAllTracks = (frequency) => {
        setFilterFrequency(frequency);
        filterNodeRefs.current.forEach(filterNode => {
            if (filterNode) {
                filterNode.frequency.value = frequency;
            }
        });
    };

    const playAllTracks = () => {
        wavesurferRefs.current.forEach(waveSurfer => {
            waveSurfer.playPause();
        });
    };

    const seekAllTracks = (seekTo) => {
        wavesurferRefs.current.forEach(waveSurfer => {
            waveSurfer.seekTo(seekTo);
        });
    };

    const changeSpeedAllTracks = (newSpeed) => {
        setSpeed(newSpeed);
        wavesurferRefs.current.forEach(waveSurfer => {
            waveSurfer.setPlaybackRate(newSpeed);
        });
    };

    const forwardAllTracks = () => {
        wavesurferRefs.current.forEach(waveSurfer => {
            const currentTime = waveSurfer.getCurrentTime();
            const duration = waveSurfer.getDuration();
            const newTime = Math.min(currentTime + 5, duration);  // Skip forward 5 seconds, but not beyond the duration
            waveSurfer.seekTo(newTime / duration);  // `seekTo` expects a value between 0 and 1
        });
    };

    const backwardAllTracks = () => {
        wavesurferRefs.current.forEach(waveSurfer => {
            const currentTime = waveSurfer.getCurrentTime();
            const newTime = Math.max(currentTime - 5, 0);  // Skip back 5 seconds, but not before the start
            const duration = waveSurfer.getDuration();
            waveSurfer.seekTo(newTime / duration);  // `seekTo` expects a value between 0 and 1
        });
    };

    return (
        <div style={pagebackground}>
            <div style={musicbackground}>
                {audioFiles.map((file, index) => (
                    <div key={index} style={waveformStyle}>
                        <div style={trackNameStyle}>{file.name}</div>
                        <div id={`waveform-${index}`} style={{ width: '100%', height: '100%' }}></div>
                    </div>
                ))}
            </div>

            {/* Sidebar with audio effects */}
            <Sidebar
                applyFilter={(frequency) => applyFilterToAllTracks(frequency)}
            />

            {audioFiles && audioFiles.length > 0 ? (
                <PlayButton
                    playAllTracks={playAllTracks}
                    forwardAllTracks={forwardAllTracks}
                    backwardAllTracks={backwardAllTracks}
                    seekAllTracks={seekAllTracks}
                    changeSpeedAllTracks={changeSpeedAllTracks}
                    isReady={isReady}
                    speed={speed}
                />
            ) : (
                <p>No audio tracks available.</p>
            )}
        </div>
    );
}

export default Landingpage;
