import React, { useState } from 'react';
import menubutton from '../images/icons/menubutton.png';
import sparkless from '../images/icons/drawstar.png';
import Menu from './Menu.js';
import AIMenu from './Ai.js';

const sidebarContainerStyle = {
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  border: '2px solid #ffffff',
  backgroundColor: 'rgba(255, 255, 255, 0.3)',
  fontFamily: "'Concert One', cursive",
  textAlign: 'left',
  fontSize: '1.2rem',
  boxShadow: '0px 0px 15px 5px rgba(255, 255, 255, 0.6)',
  position: 'fixed',
  top: '0',
  left: '0',
  zIndex: '1001',
  paddingTop: '5%',
  transition: 'width 0.3s ease', // Smooth transition when changing width
};

const expandedSidebarStyle = {
  ...sidebarContainerStyle,
  width: '25%', // Expanded width
};

const collapsedSidebarStyle = {
  ...sidebarContainerStyle,
  width: '0%', // Collapsed width
};

const buttonStyle = {
  padding: '8px 16px',
  borderRadius: '30px',
  border: '2px solid #ffffff',
  backgroundColor: 'rgba(255, 255, 255, 0.5)',
  color: '#000',
  fontFamily: "'Concert One', cursive",
  cursor: 'pointer',
  width: '90%',
  marginBottom: '10px',
  textAlign: 'center',
  fontSize: '1rem',
  boxShadow: '2px 2px 4px white, -2px -2px 4px white',
  alignSelf: 'center',
  flexDirection: 'row',
};

const menubuttonStyle = {
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'center',
  position: 'fixed',
  top: '1%',
  left: '0.5%',
  zIndex: '1001',
  cursor: 'pointer',
  backgroundColor: 'rgba(255, 255, 255, 0.0)',
  border: 'none',
  padding: '0',
  width: 'auto',
  height: 'auto',
};

const headingStyle = {
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'center',
  fontSize: '1.5rem',
  color: '#000000',
  margin: 0,
  padding: 0,
  fontFamily: 'Concert One',
  marginLeft: '10px',
  marginTop: '-2px',
  height: 'auto',
};

function Sidebar({ waveData }) {
  const [isMenuVisible, setMenuVisible] = useState(null);
  const [isSidebarExpanded, setSidebarExpanded] = useState(true); // State to control sidebar width

  const toggleMenu = (menu) => {
    setMenuVisible(isMenuVisible === menu ? null : menu);
  };

  const handleBack = () => {
    setMenuVisible(null);
  };

  const toggleSidebar = () => {
    setSidebarExpanded(!isSidebarExpanded); // Toggle between expanded and collapsed states
  };

  return (
    <div style={isSidebarExpanded ? expandedSidebarStyle : collapsedSidebarStyle}>
      <button style={menubuttonStyle} onClick={() => toggleSidebar()}>
        <img src={menubutton} alt="Menu Button" style={{ width: '33px', height: 'auto' }} />
      </button>

      {/* If the sidebar is expanded, show the menu buttons */}
      {isSidebarExpanded && (
        <>
          <button style={buttonStyle} onClick={() => toggleMenu('aiSuggestionMenu')}>
            <h1 style={headingStyle}>✨AI SUGGESTION✨</h1>
          </button>
          <button style={buttonStyle} onClick={() => toggleMenu('mixerMenu')}>
            <h1 style={headingStyle}>MIXER</h1>
          </button>
        </>
      )}

      {/* Conditionally render different menus based on which one is visible */}
      {isMenuVisible === 'mainMenu' && <Menu />}
      {isMenuVisible === 'aiSuggestionMenu' && <AIMenu handleBack={handleBack} waveData={waveData} />}
      {isMenuVisible === 'mixerMenu' && <Menu handleBack={handleBack} waveData={waveData} />}
    </div>
  );
}

export default Sidebar;
