import React, { useState } from 'react';
import menubutton from '../images/menubutton.png';
import Menu from './Menu';

const sidebarContainerStyle = {
    position: 'relative',  // Position relative to keep the button and menu together
    zIndex: '1000',  // Ensure it stays on top of other elements
};

const menubuttonStyle = {
    width: '50px',  // Adjust width as needed
    height: '50px',  // Adjust height as needed
    position: 'fixed',
    top: '0',
    right: '0',
    zIndex: '1001',  // Ensure it stays on top of the menu
    cursor: 'pointer',
    background: 'none',
    border: 'none',
    padding: '0',
};

function Sidebar() {
  const [isMenuVisible, setMenuVisible] = useState(false);

  const toggleMenu = () => {
    setMenuVisible(!isMenuVisible);
  };

  return (
    <div style={sidebarContainerStyle}>
      <button style={menubuttonStyle} onClick={toggleMenu}>
        <img src={menubutton} alt="Menu Button" style={{ width: '100%', height: '100%' }} />
      </button>
      {isMenuVisible && <Menu />}  {/* Conditionally render the Menu component */}
    </div>
  );
}

export default Sidebar;
