import React, { useState } from 'react';
import menubutton from '../images/menubutton.png';

const menubackground = {
  width: '25%',
  height: '100%',
  display: 'flex',
  flexDirection: 'column', // Stack elements vertically
  justifyContent: 'flex-start', // Align items at the top initially
  alignItems: 'left',
  border: '2px solid #ffffff',  // Sharp white border
  backgroundColor: '#ffffff',  // White background
  fontFamily: "'Concert One', cursive",  // Apply Concert One font
  fontSize: '1.2rem',
  boxShadow: '0px 0px 15px 5px rgba(255, 255, 255, 0.6)',  // Soft, glowy white shadow
  border: 'none',
  position: 'fixed',  // Fix the sidebar to the top
  top: '0',  // Position it at the top
  left: '0',  // Ensure it starts from the left edge
  zIndex: '1000',  // Ensure it stays on top of other elements
  paddingTop: '50px', // Add some space from the top
};

const backbuttonStyle = {
  display: 'flex',
  flexDirection: 'row',  
  alignItems: 'center',
  justifyContent: 'center',
  position: 'fixed', // Fixes the button position
  top: '1%',  // Adjust to add some space from the top
  left: '0.5%',
  zIndex: '1001',  // Ensure it stays on top of the menu
  cursor: 'pointer',
  backgroundColor: 'rgba(255, 255, 255, 0.0)',  // Transparent background
  border: 'none',
  padding: '0',
  width: 'auto',
  height: 'auto',
};

const textstyle = {
  color: '#000000',  // Black text color
  fontSize: '2rem',
  textAlign: 'center',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  marginTop: '50px',  // Lower the menu header by adding a margin at the top
};

const buttonStyle = {
  marginTop: '10px',
  background: '#007bff', // Blue background
  color: '#fff', // White text color
  border: 'none', // Remove default border
  padding: '10px 20px', // Add padding to make the button taller and wider
  borderRadius: '5px', // Slightly round the corners
  cursor: 'pointer', // Change cursor to pointer
  fontSize: '16px', // Adjust font size
  textAlign: 'center', // Center the text
  width: '100%', // Make button take up full width
  display: 'inline-block',
  transition: 'background 0.3s ease', // Add a transition effect for hover
};

const contentStyle = {
  flexGrow: 1, // Take up remaining space
  paddingTop: '60px', // Add padding to push content lower
};

function Menu({ handleBack }) {
  const [audioContext] = useState(new (window.AudioContext || window.webkitAudioContext)());
  const [sourceNode, setSourceNode] = useState(null);
  const [filterNode, setFilterNode] = useState(null);
  const [audioBuffer, setAudioBuffer] = useState(null);
  
  const loadAudioFile = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = function(fileEvent) {
      const arrayBuffer = fileEvent.target.result;
      audioContext.decodeAudioData(arrayBuffer, (buffer) => {
        setAudioBuffer(buffer);
      });
    };
    reader.readAsArrayBuffer(file);
  };

  const playAudio = (startTime, endTime, frequency) => {
    if (sourceNode) {
      sourceNode.stop(); // Stop any previous audio if it's playing
    }

    const newSourceNode = audioContext.createBufferSource();
    newSourceNode.buffer = audioBuffer;

    const newFilterNode = audioContext.createBiquadFilter();
    newFilterNode.type = 'lowpass';
    newFilterNode.frequency.value = frequency;

    newSourceNode.connect(newFilterNode);
    newFilterNode.connect(audioContext.destination);

    newSourceNode.start(0, startTime, endTime - startTime);
    setSourceNode(newSourceNode);
    setFilterNode(newFilterNode);
  };

  const stopAudio = () => {
    if (sourceNode) {
      sourceNode.stop();
    }
  };

  return (
    <div style={menubackground}>
      <div style={contentStyle}>
        <h1 style={textstyle}>Audio Effects Menu</h1>
        
        <label>Start Time (s):</label>
        <input type="number" id="startTime" min="0" step="0.01" defaultValue="0" />
        
        <label>End Time (s):</label>
        <input type="number" id="endTime" min="0" step="0.01" defaultValue="5" />
        
        <label>Low-pass Filter Frequency:</label>
        <input type="range" id="filterControl" min="0" max="22050" step="10" defaultValue="22050" />

        <input type="file" id="fileInput" onChange={loadAudioFile} style={{ marginTop: '20px', marginBottom: '20px' }} />

        <button
          style={buttonStyle}
          onClick={() => {
            const startTime = parseFloat(document.getElementById('startTime').value);
            const endTime = parseFloat(document.getElementById('endTime').value);
            const frequency = parseFloat(document.getElementById('filterControl').value);
            playAudio(startTime, endTime, frequency);
          }}
        >
          Play Section with Effects
        </button>
        
        <button
          style={buttonStyle}
          onClick={stopAudio}
        >
          Stop Audio
        </button>

        <button style={buttonStyle} onClick={handleBack}>Back</button>
      </div>
    </div>
  );
}

export default Menu;
