import React, { useEffect, useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import WaveSurfer from 'wavesurfer.js';
import PlayButton from '../Landingpage/PlayButton.js';
import './Exportpage.css';

const formatTime = (timeInSeconds) => {
    const minutes = Math.floor(timeInSeconds / 60);
    const seconds = Math.floor(timeInSeconds % 60);
    return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
};

function Exportpage() {
    const location = useLocation();
    const [mergedAudio, setMergedAudio] = useState(null);
    const [currentTime, setCurrentTime] = useState(0);
    const [duration, setDuration] = useState(0);
    const [isReady, setIsReady] = useState(false);
    const [speed, setSpeed] = useState(1);
    const waveformRef = useRef(null);
    const wavesurfer = useRef(null);
    const wavesurferRefs = useRef([]);
    const progressBarRef = useRef(null);

    // Web Audio API setup for beat detection
    const audioContext = useRef(new (window.AudioContext || window.webkitAudioContext)());
    const analyser = useRef(audioContext.current.createAnalyser());
    analyser.current.fftSize = 256; // Adjust FFT size for better detail

    useEffect(() => {
        if (mergedAudio && waveformRef.current) {
            wavesurfer.current = WaveSurfer.create({
                container: waveformRef.current,
                waveColor: 'rgba(255, 0, 150, 1)',
                progressColor: 'rgba(0, 204, 255, 0.5)',
                height: 100,
                barWidth: 3,
                barGap: 2,
                cursorColor: 'transparent',
                responsive: true,
                normalize: true,
            });

            wavesurfer.current.on('audioprocess', () => {
                setCurrentTime(wavesurfer.current.getCurrentTime());
            });

            wavesurfer.current.on('ready', () => {
                setDuration(wavesurfer.current.getDuration());
                setIsReady(true);
                wavesurferRefs.current.push(wavesurfer.current);

                // Connect WaveSurfer to AnalyserNode for frequency data
                const source = audioContext.current.createMediaElementSource(wavesurfer.current.backend.media);
                source.connect(analyser.current);
                analyser.current.connect(audioContext.current.destination);
                animateVisualizer();
            });

            wavesurfer.current.load(mergedAudio);
        }
    }, [mergedAudio]);

    useEffect(() => {
        if (location.state && location.state.mergedAudio) {
            setMergedAudio(location.state.mergedAudio);
        }
    }, [location.state]);

    const animateVisualizer = () => {
        const bufferLength = analyser.current.frequencyBinCount;
        const dataArray = new Uint8Array(bufferLength);

        const draw = () => {
            requestAnimationFrame(draw);
            analyser.current.getByteFrequencyData(dataArray);

            // Dynamically adjust bar height based on frequency data
            const bars = waveformRef.current.querySelectorAll('wave');
            for (let i = 0; i < bars.length; i++) {
                bars[i].style.height = `${Math.max(dataArray[i] / 2, 1)}px`;
            }
        };

        draw();
    };

    const handleTimelineChange = (e) => {
        const newTime = e.target.value;
        setCurrentTime(newTime);
        if (wavesurfer.current) {
            wavesurfer.current.setCurrentTime(newTime);
        }
    };

    return (
        <div className="pagebackground">
            <div className="circle-shape"></div>

            <div id="waveform" ref={waveformRef} className="waveform"></div>

            <div className="timeline-container">
                <input
                    type="range"
                    min="0"
                    max={duration}
                    value={currentTime}
                    step="0.01"
                    onChange={handleTimelineChange}
                    className="timeline-slider"
                    ref={progressBarRef}
                />
                <div className="time-display">
                    <span>{formatTime(currentTime)}</span>
                    <span>{formatTime(duration)}</span>
                </div>
            </div>

            <div className="play-button-container">
                <PlayButton
                    wavesurferRefs={wavesurferRefs}
                    setSpeed={setSpeed}
                    isReady={isReady}
                    speed={speed}
                />
            </div>
        </div>
    );
}

export default Exportpage;
