import React from 'react';
import menubutton from '../images/menubutton.png';

const menubackground = {
    width: '50px',  // Adjust width as needed
    height: '50px',  // Adjust height as needed
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    border: 'none',  // Remove border
    backgroundColor: 'rgba(255, 255, 255, 0.3)',  // Slightly transparent white background
    boxShadow: '0px 0px 15px 5px rgba(255, 255, 255, 0.6)',  // Soft, glowy white shadow
    position: 'fixed',  // Fix the button to the screen
    top: '0',  // Position it at the top
    right: '0',  // Align it to the right edge
    zIndex: '1000',  // Ensure it stays on top of other elements
    cursor: 'pointer',  // Ensure cursor changes to pointer on hover
};


const textstyle = {
    color: '#000000',  // Black text color
    fontSize: '2rem',
    marginBottom: '10px',  // Space below the heading
    marginTop: '0',  // Space above the heading
    textAlign: 'left',
};

const buttonStyle = {
    background: 'none',  // Remove default button background
    border: 'none',  // Remove default button border
    padding: '0',  // Remove default button padding
    cursor: 'pointer',  // Ensure cursor changes to pointer on hover
};

function Menu() {
  return (
    <div style={menubackground}>
        <button style={buttonStyle} onClick={() => console.log('Menu button clicked!')}>
            <img src={menubutton} alt="Menu Button" style={{ width: '90%', height: '7%' }} />
        </button>
    </div>
  );
}

export default Menu;
