import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import './Exportpage.css';  // Ensure this points to the correct path

function Exportpage() {
    const location = useLocation();
    const [mergedAudio, setMergedAudio] = useState(null);

    // Ensure mergedAudio is set when location.state changes
    useEffect(() => {
        if (location.state && location.state.mergedAudio) {
            setMergedAudio(location.state.mergedAudio);
        }
    }, [location.state]);

    return (
        <div className="pagebackground">
            <div className="player-card">
                {/* You can add an image here if you want to resemble the background */}
                <img src={require('../images/image.png')} alt="Player background" />

                {/* Audio Player */}
                {mergedAudio ? (
                    <div className="player-controls">
                        <button>⏮️</button>
                        <button>⏯️</button>
                        <button>⏭️</button>
                    </div>
                ) : (
                    <p>Loading audio...</p>
                )}

                {/* Audio Element */}
                {mergedAudio && (
                    <audio controls>
                        <source src={mergedAudio} type="audio/wav" />
                        Your browser does not support the audio element.
                    </audio>
                )}
            </div>
        </div>
    );
}

export default Exportpage;
