import React, { useRef, useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import WaveSurfer from 'wavesurfer.js';
import WebAudioPlayer from 'wavesurfer.js/dist/webaudio.js';
import RegionsPlugin from 'wavesurfer.js/dist/plugins/regions.js';
import TimelinePlugin from 'wavesurfer.js/dist/plugins/timeline';
import Sidebar from './Sidebar.js';
import PlayButton from './PlayButton.js';
import './Landingpage.css';

const Landingpage = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const wavesurferRefs = useRef([]);
    const containerRefs = useRef([]);
    const [audioFiles, setAudioFiles] = useState(location.state?.audioFiles || []);
    const [isReady, setIsReady] = useState(false);
    const [speed, setSpeed] = useState(1);
    const [longestDuration, setLongestDuration] = useState(0);

    const initializeWaveSurfers = () => {
        wavesurferRefs.current = [];

        if (audioFiles.length > 0) {
            audioFiles.forEach((file, index) => {
                const container = containerRefs.current[index];
                if (!container) {
                    console.warn(`No container for track at index ${index}`);
                    return;
                }

                const waveSurfer = WaveSurfer.create({
                    container,
                    waveColor: '#1E90FF',
                    progressColor: '#00CED1',
                    height: 75,
                    responsive: true,
                    cursorWidth: 2,
                    cursorColor: '#FF4500',
                    plugins: [
                        RegionsPlugin.create(),
                        TimelinePlugin.create({
                            container: container.previousSibling,
                            primaryColor: '#FFFFFF',
                            secondaryColor: '#AAAAAA',
                        }),
                    ],
                });

                waveSurfer.on('ready', () => {
                    const duration = waveSurfer.getDuration();
                    setLongestDuration((prev) => Math.max(prev, duration));
                    setIsReady(true);
                });

                waveSurfer.on('region-click', (region, event) => {
                    event.stopPropagation();
                    region.remove();
                });

                wavesurferRefs.current[index] = waveSurfer;
                waveSurfer.load(file.url);
            });
        }
    };

    useEffect(() => {
        if (audioFiles.length > 0 && wavesurferRefs.current.length === 0) {
            setTimeout(initializeWaveSurfers, 100);
        }
    }, [audioFiles]);

    const handleAddFiles = (event) => {
        const newFiles = Array.from(event.target.files).map((file) => ({
            name: file.name,
            url: URL.createObjectURL(file),
            type: file.type,
        }));

        if (newFiles.length > 0) {
            setAudioFiles((prev) => [...prev, ...newFiles]);
        } else {
            alert('No files selected!');
        }
    };

    const mergeAudioFiles = () => {
        if (wavesurferRefs.current.length > 0) {
            const buffers = wavesurferRefs.current.map((ws) => ws.getDecodedData());
            // Custom merging logic here...
            const mergedBlob = new Blob(buffers, { type: 'audio/wav' });
            navigate('/Exportpage', { state: { mergedAudio: URL.createObjectURL(mergedBlob) } });
        }
    };

    return (
        <div className="page">
            <div className="waveform-container">
                {audioFiles.map((file, index) => (
                    <div key={index} className="track-wrapper">
                        <div className="track-name">{file.name}</div>
                        <div className="timeline"></div>
                        <div
                            className="waveform"
                            ref={(el) => (containerRefs.current[index] = el)}
                        ></div>
                    </div>
                ))}
            </div>

            <Sidebar waveData={wavesurferRefs.current} />

            {audioFiles.length > 0 ? (
                <PlayButton
                    wavesurferRefs={wavesurferRefs}
                    setSpeed={setSpeed}
                    isReady={isReady}
                    speed={speed}
                />
            ) : (
                <div className="no-files-message">No tracks loaded yet!</div>
            )}

            <div className="controls">
                <button
                    className="add-files-button"
                    onClick={() => document.getElementById('fileInput').click()}
                >
                    Add Tracks
                </button>
                <button className="export-button" onClick={mergeAudioFiles}>
                    Export Tracks
                </button>
                <input
                    id="fileInput"
                    type="file"
                    multiple
                    accept="audio/*"
                    onChange={handleAddFiles}
                    style={{ display: 'none' }}
                />
            </div>
        </div>
    );
};

export default Landingpage;
