import React, {useState}  from 'react';

const menubackground = {
  width: '25%',
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'flex-start',
  alignItems: 'left',
  border: '2px solid #ffffff',
  backgroundColor: '#ffffff',
  fontFamily: "'Concert One', cursive",
  fontSize: '1.2rem',
  boxShadow: '0px 0px 15px 5px rgba(255, 255, 255, 0.6)',
  border: 'none',
  position: 'fixed',
  top: '0',
  left: '0',
  zIndex: '1000',
  paddingTop: '20px',
  overflowY: 'auto', // Enable vertical scrolling
  maxHeight: '100%', // Set a maximum height for the menu
};

const backButtonStyle = {
  marginTop: '10px',
  background: '#007bff',
  color: '#fff',
  border: 'none',
  padding: '10px 20px',
  borderRadius: '5px',
  cursor: 'pointer',
  fontSize: '16px',
  textAlign: 'center',
  width: 'auto',
  display: 'inline-block',
  transition: 'background 0.3s ease',
};

const reverseButtonStyle = {
  ...backButtonStyle,
  background: '#ff5722', // Different color for reverse button
};

const sliderStyle = {
  width: '80%',
  margin: '10px auto',
};

const adjustGainButtonStyle = {
  ...backButtonStyle,
  background: '#4caf50', // Different color for adjust gain button
};


const modalStyle = {
  position: 'fixed',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '300px',
  backgroundColor: '#fff',
  padding: '20px',
  borderRadius: '10px',
  boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.3)',
  zIndex: '1001',
};

const overlayStyle = {
  position: 'fixed',
  top: '0',
  left: '0',
  right: '0',
  bottom: '0',
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  zIndex: '1000',
};

const contentStyle = {
  flexGrow: 1,
  paddingTop: '20px',
};
var toWav = require('audiobuffer-to-wav');
var utils = require("audio-buffer-utils");
function bufferToWavURL(buffer) {
  let wavURL = "";
  var blob = new window.Blob([new DataView(toWav(buffer))], {type: "audio/wav"});
  wavURL = window.URL.createObjectURL(blob);
  return wavURL;
}

function Menu({ handleBack, waveData }) {
  const [isGainModalOpen, setGainModalOpen] = useState(false);
  const [gainValue, setGainValue] = useState(1);
  
  const reverse = () => {
    let buffer = waveData[0].waveSurfer.getDecodedData();
    let region = (waveData[0].regions.getRegions())[0];
    if (buffer) {
      utils.reverse(buffer, region.start, region.end);
      console.log("Reversed region.");
      let wavURL = bufferToWavURL(buffer);
      console.log(wavURL);
      const a = document.createElement("a");
      a.href = wavURL;
      a.download = `generated_music.wav`;
  
      // Trigger download
      document.body.appendChild(a);
      a.click();
      a.remove();
      URL.revokeObjectURL(wavURL);
    } else {
      console.error('No audio buffer loaded');
    }
  };

  const invert = () => {
    let buffer = waveData[0].waveSurfer.getDecodedData();
    let region = (waveData[0].regions.getRegions())[0];
    if (buffer) {
      utils.invert(buffer, region.start, region.end);
      console.log("Inverted region.");
    } else {
      console.error('No audio buffer loaded');
    }
  };

  const normalize = () => {
    let buffer = waveData[0].waveSurfer.getDecodedData();
    let region = (waveData[0].regions.getRegions())[0];
    if (buffer) {
      utils.normalize(buffer, region.start, region.end);
      console.log("Normalized region.");
    } else {
      console.error('No audio buffer loaded');
    }
  };

  const openGainModal = () => setGainModalOpen(true);
  const closeGainModal = () => setGainModalOpen(false);

  const adjustGain = (e) => {
    let buffer = waveData[0].waveSurfer.getDecodedData();
    let region = (waveData[0].regions.getRegions())[0];
    const newGainValue = parseFloat(e.target.value);

    if (buffer) {
      utils.fill(buffer, (value, i, channel) => value[i]*newGainValue, region.start * 1000, region.end * 1000);
      console.log('Gain adjusted:', newGainValue);
    }
  };

  return (
    <div style={menubackground}>
      <div style={contentStyle}>
        <button style={backButtonStyle} onClick={handleBack}>Back</button>

        <button style={reverseButtonStyle} onClick={reverse}>
          Reverse Selected Region
        </button>

        <button style={reverseButtonStyle} onClick={invert}>
          Invert Selected Region
        </button>

        <button style={reverseButtonStyle} onClick={normalize}>
          Normalize Selected Region
        </button>

        <button style={adjustGainButtonStyle} onClick={openGainModal}>
          Adjust Gain
        </button>

        {isGainModalOpen && (
          <>
            <div style={overlayStyle} onClick={closeGainModal} />
            <div style={modalStyle}>
              <h2>Adjust Gain</h2>
              <input
                type="range"
                min="0"
                max="2"
                step="0.01"
                value={gainValue}
                onChange={adjustGain}
                style={sliderStyle}
              />
              <button onClick={closeGainModal} style={backButtonStyle}>Close</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default Menu;
