import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import './Exportpage.css';  // Ensure this points to the correct path

function Exportpage() {
    const location = useLocation();
    const [mergedAudio, setMergedAudio] = useState(null);

    // Ensure mergedAudio is set when location.state changes
    useEffect(() => {
        if (location.state && location.state.mergedAudio) {
            setMergedAudio(location.state.mergedAudio);
        }
    }, [location.state]);

    return (
        <div className="pagebackground">
            {/* Heart-shaped image */}
            <div className="heart-shaped"></div>

            {/* Audio Element */}
            {mergedAudio ? (
                <audio controls>
                    <source src={mergedAudio} type="audio/wav" />
                    Your browser does not support the audio element.
                </audio>
            ) : (
                <p>Loading audio...</p>
            )}
        </div>
    );
}

export default Exportpage;
