import React, { useEffect, useRef, useState } from 'react';
import WaveSurfer from 'wavesurfer.js';
import { useLocation } from 'react-router-dom';
import Sidebar from './Sidebar.js';
import cloud from '../images/cloud.png';
import PlayButton from './PlayButton.js';

const pagebackground = {
    backgroundSize: 'cover',
    backgroundImage: `url(${cloud})`,
    backgroundPosition: 'center',
    padding: '10px',
    height: '100vh',
    margin: 0,
    display: 'flex',
    flexDirection: 'column',
    fontFamily: 'Concert One',
};

const musicbackground = {
    width: '75%',
    height: '80%',
    position: 'fixed',
    top: '0',
    right: '0',
    display: 'flex',
    flexDirection: 'column',  // Stack waveforms vertically
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    backgroundColor: 'rgba(255, 255, 255, 0.3)',
    marginTop: '5px',
    boxShadow: '0px 0px 15px 5px rgba(255, 255, 255, 0.6)',
    overflowY: 'auto',  // Enable vertical scrolling if needed
};

const trackNameStyle = {
    color: '#000000',
    fontSize: '0.8rem',
    padding: '5px',
    borderRadius: '5px',
};

const waveformStyle = {
    width: '100%',
    height: '75px',  // Fixed height for each waveform
    position: 'relative',
    marginBottom: '10px',
};

function Landingpage() {
    const wavesurferRefs = useRef([]); // Array to hold refs for each WaveSurfer instance
    const location = useLocation();
    const { audioFiles } = location.state || { audioFiles: [] };
    const [isReady, setIsReady] = useState(false);

    useEffect(() => {
        if (audioFiles && audioFiles.length > 0) {
            wavesurferRefs.current = audioFiles.map(() => React.createRef());  // Create refs for each file

            audioFiles.forEach((file, index) => {
                const container = wavesurferRefs.current[index].current;
                if (container) {
                    const waveSurfer = WaveSurfer.create({
                        container,
                        waveColor: 'blue',
                        progressColor: '#00FFFF',
                        height: 75,
                        autoCenter: true,
                        interact: true,
                        backend: 'MediaElement',
                        cursorWidth: 2,
                        cursorColor: '#FF0000',
                        backgroundColor: 'rgba(255, 255, 255, 255)',
                        minPxPerSec: 100,
                    });

                    waveSurfer.load(file.url);

                    waveSurfer.on('ready', () => {
                        setIsReady(true);
                    });

                    wavesurferRefs.current[index].waveSurfer = waveSurfer;
                }
            });

            // Cleanup function to properly handle the destruction of WaveSurfer instances
            return () => {
                wavesurferRefs.current.forEach(ref => {
                    if (ref.waveSurfer) {
                        ref.waveSurfer.destroy();
                    }
                });
            };
        }
    }, [audioFiles]);

    const playAllTracks = () => {
        wavesurferRefs.current.forEach(ref => {
            if (ref.waveSurfer) {
                ref.waveSurfer.play();
            }
        });
    };

    return (
        <div style={pagebackground}>
            <div style={musicbackground}>
                {audioFiles.map((file, index) => (
                    <div key={index}>
                        <div style={trackNameStyle}>{file.name}</div>
                        <div ref={wavesurferRefs.current[index]} style={waveformStyle}></div>
                    </div>
                ))}
            </div>

            <Sidebar/>

            {audioFiles.length > 0 && (
                <PlayButton
                    onClick={playAllTracks}
                    isReady={isReady}
                />
            )}
        </div>
    );
}

export default Landingpage;
