import React, { useState } from "react";

const menubackground = { /* styles remain unchanged */ };
const generateButtonStyle = { /* styles remain unchanged */ };
const descriptionBoxStyle = { /* styles remain unchanged */ };
const backButtonStyle = { /* styles remain unchanged */ };
const contentStyle = { /* styles remain unchanged */ };

function AIMenu({ handleBack }) {
  const [loading, setLoading] = useState(false);
  const [description, setDescription] = useState("");
  const [errorMessage, setErrorMessage] = useState(null);

  const handleGenerateMusic = async () => {
    setLoading(true);
    setErrorMessage(null);

    try {
      console.log("Sending request to generate music with description:", description);

      // Correcting the API endpoint and payload to align with testApi.js
      const response = await fetch("https://facebook-musicgen.hf.space/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          fn_index: 0,
          data: [description] // Ensure this is in the format expected by the API
        }),
      });

      if (!response.ok) {
        throw new Error(`Fetch failed with status: ${response.status}`);
      }

      const result = await response.json();
      console.log("Generated Music Result:", result);

      const wavFilePath = result?.data?.[1]?.name;
      if (!wavFilePath) {
        throw new Error("No WAV file found in the result.");
      }

      const wavFileUrl = `https://facebook-musicgen.hf.space/file=${wavFilePath}`;
      console.log("WAV file URL:", wavFileUrl);

      await downloadFile(wavFileUrl, description);
    } catch (error) {
      console.error("Error generating or downloading music:", error);
      setErrorMessage(`Failed to generate or download music: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const downloadFile = async (fileUrl, description) => {
    try {
      const response = await fetch(fileUrl);
      if (!response.ok) {
        throw new Error("Failed to download the music file.");
      }

      const blob = await response.blob();
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `${description}_generated_music.wav`;
      document.body.appendChild(a);
      a.click();
      a.remove();
      URL.revokeObjectURL(url);
    } catch (error) {
      console.error("Error downloading the file:", error);
      alert(`Error downloading the file: ${error.message}`);
    }
  };

  return (
    <div style={menubackground}>
      <div style={contentStyle}>
        <h1>This Is The AI Menu</h1>
        {errorMessage && <div>{errorMessage}</div>}
        <textarea
          style={descriptionBoxStyle}
          placeholder="Describe your music..."
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <button style={generateButtonStyle} onClick={handleGenerateMusic} disabled={loading}>
          {loading ? "Generating..." : "Generate Music"}
        </button>
        <button style={backButtonStyle} onClick={handleBack}>Back</button>
      </div>
    </div>
  );
}

export default AIMenu;
