import React, { useState } from 'react';
import fastforward from '../images/icons/fast-forward.png';
import pause from '../images/icons/pause.png';
import play from '../images/icons/play.png';
import rewindstart from '../images/icons/startofmusic.png';
import backwards from '../images/icons/backwards.png';
import endofmusic from '../images/icons/endofmusic.png';

const buttonContainerStyle = {
    height: '20%',
    width: '75%',
    display: 'flex',
    flexDirection: 'row',
    position: 'fixed',
    right: '0px',
    bottom: '0px',
    alignItems: 'center',
    boxShadow: '0px 0px 15px 5px rgba(255, 255, 255, 0.6)',
    backgroundColor: 'rgba(255, 255, 255, 0.15)',
    justifyContent: 'center',
    padding: '10px',
    borderRadius: '10px',
};

const buttonStyle = {
    background: 'transparent',
    border: '2px solid rgba(255, 255, 255, 0.6)',
    borderRadius: '50%',
    margin: '0 5px',
    padding: '15px',
    cursor: 'pointer',
    transition: 'transform 0.2s',
};

const iconStyle = {
    width: '35px',
    height: '35px',
    filter: 'drop-shadow(0 0 3px rgba(255, 255, 255, 0.8))',
};

function PlayButton({ wavesurferRefs, isReady }) {
    const [isPlaying, setIsPlaying] = useState(false);

    const togglePlayback = () => {
        wavesurferRefs.current.forEach((waveSurfer) => {
            waveSurfer.playPause();
        });
        setIsPlaying((prev) => !prev);
    };

    const adjustTracks = (type) => {
        wavesurferRefs.current.forEach((waveSurfer) => {
            const currentTime = waveSurfer.getCurrentTime();
            const duration = waveSurfer.getDuration();
            const timeChange = type === 'forward' ? 5 : -5;
            const newTime = Math.min(Math.max(currentTime + timeChange, 0), duration);
            waveSurfer.seekTo(newTime / duration);
        });
    };

    const seekTo = (position) => {
        wavesurferRefs.current.forEach((waveSurfer) => {
            const isCurrentlyPlaying = waveSurfer.isPlaying();
            if (isCurrentlyPlaying) {
                waveSurfer.pause();
            }
            waveSurfer.seekTo(position);
            if (isCurrentlyPlaying) {
                waveSurfer.play();
            }
        });
    };

    return (
        <div style={buttonContainerStyle}>
            <button
                onClick={() => seekTo(0)}
                style={buttonStyle}
                disabled={!isReady}
            >
                <img src={rewindstart} alt="Go to Start" style={iconStyle} />
            </button>
            <button
                onClick={() => adjustTracks('backward')}
                style={buttonStyle}
                disabled={!isReady}
            >
                <img src={backwards} alt="Backward 5s" style={iconStyle} />
            </button>
            <button
                onClick={togglePlayback}
                style={buttonStyle}
                disabled={!isReady}
            >
                <img
                    src={isPlaying ? pause : play}
                    alt="Play/Pause"
                    style={iconStyle}
                />
            </button>
            <button
                onClick={() => adjustTracks('forward')}
                style={buttonStyle}
                disabled={!isReady}
            >
                <img src={fastforward} alt="Forward 5s" style={iconStyle} />
            </button>
            <button
                onClick={() => seekTo(1)}
                style={buttonStyle}
                disabled={!isReady}
            >
                <img src={endofmusic} alt="Go to End" style={iconStyle} />
            </button>
        </div>
    );
}

export default PlayButton;
