import React from 'react';
import './Homepage.js'; // Import the CSS file for this component



const headingStyle = {
  color: '#000000',
  fontSize: '3rem',
  marginBottom: '-10px',  // Reduce space below the heading

};

const paragraphStyle = {
  color: '#666',
  fontSize: '1.2rem',
  marginTop: '0px',  // Reduce space above the paragraph


};


function HomePage() {
  return (
    <div > {/* Apply pageStyle to the div */}
      <h1 style={headingStyle}>Mellowdies</h1> {/* Apply headingStyle to the h1 */}
      <p style={paragraphStyle}>Welcome To Your Audio Editing Platform!</p> {/* Apply paragraphStyle to the p */}
      <p style={paragraphStyle}>Upload File</p> {/* Apply paragraphStyle to the p */}


    </div>
  );
}

export default HomePage;
