import React, { useState } from 'react';
import menubutton from '../images/icons/menubutton.png';
import sparkless from '../images/icons/drawstar.png';
import Menu from './Menu.js';
import AIMenu from './Ai.js';

const sidebarContainerStyle = {
    width: '25%',
    height: '100%',
    display: 'flex',
    justifyContent: 'left',
    alignItems: 'center', 
    flexDirection: 'column',  
    border: '2px solid #ffffff',
    backgroundColor: 'rgba(255, 255, 255, 0.3)',  
    fontFamily: "'Concert One', cursive",
    textAlign: 'left',
    fontSize: '1.2rem',
    boxShadow: '0px 0px 15px 5px rgba(255, 255, 255, 0.6)',
    position: 'fixed',
    top: '0',
    left: '0',
    zIndex: '1001',
    paddingTop: '5%',
    transition: 'width 0.3s ease',
};

const expandedSidebarStyle = {
  ...sidebarContainerStyle,
  width: '25%', 
};

const collapsedSidebarStyle = {
  ...sidebarContainerStyle,
  width: '0%',
};

const buttonStyle = {
  backgroundColor: '#58336a',  // Base background color
  color: '#fff',               // White text
  border: 'none',              // Remove border for a clean look
  padding: '12px 24px',        // More spacious padding for better clickability
  borderRadius: '8px',         // Rounded corners for modern appearance
  cursor: 'pointer',           // Pointer cursor for interactivity
  fontSize: '16px',             // Standard font size
  fontWeight: '500',            // Slightly bold font weight for prominence
  textAlign: 'center',         // Center the text
  display: 'inline-block',      // Inline block display
  transition: 'all 0.3s ease',  // Smooth transition for hover and active states
  margin: '10px 0',             // Spacing between buttons
  width: 'auto',                // Auto width based on content
};

const buttonHoverStyle = {
  backgroundColor: '#7c4d91', // Lighter background on hover
  transform: 'scale(1.05)',    // Slightly scale up for hover interaction
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)', // Subtle shadow for depth on hover
};
const buttonFocusStyle = {
  outline: 'none',  // Remove default focus outline
  boxShadow: '0 0 0 3px rgba(88, 51, 106, 0.5)', // Custom focus outline for accessibility
};

// Active State Style
const buttonActiveStyle = {
  transform: 'scale(0.98)',  // Slight shrink on active for click feedback
  boxShadow: 'none',         // Remove shadow when clicked
};
const menubuttonStyle = {
    display: 'flex',
    flexDirection: 'row',  
    alignItems: 'center',
    justifyContent: 'center',
    position: 'fixed',
    top: '1%',  
    left: '0.5%',
    zIndex: '1001',  
    cursor: 'pointer',
    backgroundColor: 'rgba(255, 255, 255, 0.0)',  
    border: 'none',
    padding: '0',
    width: 'auto',
    height: 'auto',
};

const headingStyle = {
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'center',
  fontSize: '1.5rem',
  color: '#000000',
  fontFamily: 'Concert One',
  margin: '0 0 0 10px',
  padding: 0,
  height: 'auto',
};


function Sidebar({ waveData, fileLoaded }) {
  const [isMenuVisible, setMenuVisible] = useState(null);
  const [isFileWarningVisible, setFileWarningVisible] = useState(false);
  const [isSidebarExpanded, setSidebarExpanded] = useState(true);

  const toggleMenu = (menu) => {
    if (menu === 'mixerMenu' && !fileLoaded) {
      // Show warning and prevent menu from opening
      setFileWarningVisible(true);
    } else {
      // Open the menu if conditions are met
      setMenuVisible(isMenuVisible === menu ? null : menu);
      setFileWarningVisible(false);
    }
  };

  const handleBack = () => {
    setMenuVisible(null);
    setFileWarningVisible(false);
  };

  const toggleSidebar = () => {
    setSidebarExpanded(!isSidebarExpanded);
  };

  return (
    <div style={isSidebarExpanded ? expandedSidebarStyle : collapsedSidebarStyle}>
      <button style={menubuttonStyle} onClick={() => toggleSidebar()}>
        <img src={menubutton} alt="Menu Button" style={{ width: '33px', height: 'auto' }} />
        <h1 style={headingStyle}>MELLOWDIES</h1>
      </button>

      {isSidebarExpanded && (
        <>
          {isMenuVisible === null && (
            <>
              <button style={buttonStyle} onClick={() => toggleMenu('aiSuggestionMenu')}>
                <h1 style={headingStyle}>AI EDITOR</h1>
              </button>
              <button style={buttonStyle} onClick={() => toggleMenu('mixerMenu')}>
                <h1 style={headingStyle}>MIXER</h1>
              </button>
            </>
          )}
          {isMenuVisible === 'mainMenu' && <Menu />}
          {isMenuVisible === 'aiSuggestionMenu' && (
            <AIMenu handleBack={handleBack} waveData={waveData} />
          )}
          {isMenuVisible === 'mixerMenu' && <Menu handleBack={handleBack} waveData={waveData} />}
          {isFileWarningVisible && (
            <div
              style={{
                color: 'red',
                textAlign: 'center',
                marginTop: '10px',
                fontSize: '1rem',
                backgroundColor: 'rgba(255, 0, 0, 0.1)',
                padding: '10px',
                borderRadius: '8px',
              }}
            >
              <p>Warning: Please load a file before accessing the Mixer!</p>
              <button
                style={{
                  marginTop: '5px',
                  padding: '5px 10px',
                  borderRadius: '4px',
                  border: 'none',
                  backgroundColor: 'red',
                  color: 'white',
                  cursor: 'pointer',
                }}
                onClick={() => setFileWarningVisible(false)}
              >
                Dismiss
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default Sidebar;
