import React from 'react';
import gifBackground from './images/skybackground.png';  // Adjust the path if needed

const pageStyle = {
  backgroundImage: `url(${gifBackground})`,  // Set the image as the background
  backgroundSize: 'cover',  // Ensures the image covers the entire page
  backgroundPosition: 'center',  // Centers the background
  textAlign: 'center',
  padding: '20px',  // Adjust the padding
  height: '100vh',  // Full viewport height
  margin: 0,
  display: 'flex',
  flexDirection: 'column',  // Arrange children in a column
  alignItems: 'center',
  fontFamily: 'Concert One',  // Use Concert One font
};

const headingStyle = {
    color: '#000000',  // Black text color
    fontSize: '3rem',
    textShadow: '3px 3px 5px pink, -3px -3px 5px pink, 3px -3px 5px pink, -3px 3px 5px pink',  // Larger pink outline effect
    marginBottom: '10px',  // Space below the heading
    marginTop: '0',  // Space above the heading
  };
  

const paragraphStyle = {
  color: '#111111',
  fontSize: '1.2rem',
  margin: '5px 0',  // Even out margins for the paragraph
};

const containerStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'flex-start',  // Align text at the top
  width: '100%',  // Full width
  paddingTop: '50px',  // Add some padding from the top
};

const uploadStyle = {
    marginTop: '20px',  // Space above the upload box
    padding: '10px',
    borderRadius: '30px',
    border: '2px solid #ffffff',  // Sharp white border
    fontSize: '1.5rem',
    cursor: 'pointer',
    backgroundColor: 'rgba(255, 255, 255, 0.5)',  // Slightly transparent white background
    boxShadow: '0 0 10px rgba(255, 255, 255, 0.9)',  // Stronger glowing white edges effect
    color: '#000',  // Ensures the text inside is still visible
    fontFamily: 'Concert One',  // Use Concert One font
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',

  };const hiddenFileInput = {
    display: 'none',  // Hide the default file input button
  };
  
  const customButtonStyle = {
    display: 'inline-block',
    padding: '10px 20px',
    backgroundColor: '#ffffff',  // White background for the button
    color: '#000',  // Black text color
    fontFamily: "'Concert One', cursive",  // Apply Concert One font to the label
    borderRadius: '5px',
    border: '2px solid #000',
    cursor: 'pointer',  // Pointer cursor for the label
    textAlign: 'center',
  };
  
  function HomePage() {
    const handleFileUpload = (event) => {
      console.log(event.target.files[0]); // Handle the file upload
    };
  
    return (
      <div style={uploadStyle}>
        <label htmlFor="file-upload" style={customButtonStyle}>
          Choose File
        </label>
        <input
          id="file-upload"
          type="file"
          style={hiddenFileInput}
          onChange={handleFileUpload}
        />
      </div>
    );
  }
  
  export default HomePage;
