import React, { useEffect, useRef, useState } from 'react';
import WaveSurfer from 'wavesurfer.js';
import TimelinePlugin from "wavesurfer.js/dist/plugins/timeline";
import { useLocation } from 'react-router-dom';
import Sidebar from './Sidebar.js';
import './Landingpage.css';  
import PlayButton from './PlayButton.js';
import { sliceAudio, deleteAudioSection, handleTrackClick } from './Audiotrack.js'; 

function Landingpage() {
    const wavesurferRefs = useRef([]);  
    const location = useLocation();
    const { audioFiles } = location.state || { audioFiles: [] };
    const [isReady, setIsReady] = useState(false);
    const [waveformWidths, setWaveformWidths] = useState([]);  

    useEffect(() => {
        wavesurferRefs.current = [];
        let widths = [];

        if (audioFiles && audioFiles.length > 0) {
            audioFiles.forEach((file, index) => {
                const containerId = `waveform-${index}`;

                const waveSurfer = WaveSurfer.create({
                    container: `#${containerId}`,
                    waveColor: 'blue',
                    progressColor: '#00FFFF',
                    height: 75,
                    autoCenter: true,
                    interact: true,
                    backend: 'MediaElement',
                    cursorWidth: 2,
                    cursorColor: '#FF0000',
                    backgroundColor: 'rgba(255, 255, 255, 0)',
                    minPxPerSec: 100,  // Controls the width scaling
                });

                waveSurfer.load(file.url);

                waveSurfer.on('ready', () => {
                    setIsReady(true);
                    const duration = waveSurfer.getDuration();

                    // Calculate width based on duration
                    const width = duration * 100;  // Use this formula to adjust the scaling
                    widths.push(width);
                    setWaveformWidths([...widths]);

                    if (index === 0) {
                        waveSurfer.addPlugin(TimelinePlugin.create({
                            container: `#timeline`,
                            duration: duration,
                            timeInterval: Math.ceil(duration / 10),
                            primaryLabelInterval: Math.ceil(duration / 5),
                            secondaryLabelInterval: Math.ceil(duration / 10),
                            formatTimeCallback: (seconds) => {
                                const minutes = Math.floor(seconds / 60);
                                const remainingSeconds = Math.floor(seconds % 60);
                                return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
                            },
                            style: {
                                color: '#000',
                                fontSize: '12px',
                                fontFamily: 'Arial',
                            },
                            height: 30,
                            insertPosition: 'beforeend',
                        })).initPlugin('timeline');
                    }
                });

                waveSurfer.on('seek', (progress) => {
                    const duration = waveSurfer.getDuration();
                    const clickedTime = progress * duration;

                    const startTime = clickedTime;
                    const endTime = startTime + 5;

                    sliceAudio(waveSurfer, startTime, endTime);
                });

                // Store the waveSurfer instance in the refs array
                wavesurferRefs.current[index] = waveSurfer;
            });

            // Cleanup function
            return () => {
                wavesurferRefs.current.forEach(waveSurfer => {
                    if (waveSurfer) {
                        waveSurfer.destroy();
                    }
                });
            };
        }
    }, [audioFiles]);

    return (
        <div className="pagebackground">
            <div id="timeline" className="timelineStyle"></div>

            <div className="musicbackground">
                {audioFiles.map((file, index) => (
                    <div key={index} className="waveformStyle" style={{ width: `${waveformWidths[index]}px` }}>
                        <div id={`waveform-${index}`} style={{ height: '100%' }}></div>
                    </div>
                ))}
            </div>

            <Sidebar/>

            {audioFiles && audioFiles.length > 0 ? (
                <PlayButton
                    wavesurferRefs={wavesurferRefs}
                    setSpeed={setSpeed}
                    isReady={isReady}
                    speed={speed}
                />
            ) : (
                <p>No audio tracks available.</p>
            )}
        </div>
    );
}

export default Landingpage;
