import React, { useState } from 'react';
import menubutton from '../images/menubutton.png';
import Menu from './Menu';

const sidebarbackground = {
    width: '25%',
    height: '100%',
    display: 'flex',
    justifyContent: 'left',
    alignItems: 'left',
    border: '2px solid #ffffff',
    backgroundColor: '#ffffff',
    fontFamily: "'Concert One', cursive",
    textAlign: 'left',
    fontSize: '1.2rem',
    boxShadow: '0px 0px 15px 5px rgba(255, 255, 255, 0.6)',
    position: 'fixed',
    top: '0',
    left: '0',
    zIndex: '1000',
};

const buttonStyle = {
    background: 'none',
    border: 'none',
    padding: '0',
    cursor: 'pointer',
};

function Sidebar() {
  const [isMenuVisible, setMenuVisible] = useState(false);

  const toggleMenu = () => {
    setMenuVisible(!isMenuVisible);
  };

  return (
    <div style={sidebarbackground}>
        <button style={buttonStyle} onClick={toggleMenu}>
            <img src={menubutton} alt="Menu Button" style={{ width: '90%', height: '7%' }} />
        </button>
        {isMenuVisible && <Menu />}  {/* Conditionally render the Menu component */}
    </div>
  );
}

export default Sidebar;
