import React, { useEffect, useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import WaveSurfer from 'wavesurfer.js';
import TimelinePlugin from 'wavesurfer.js/dist/plugins/timeline';
import './Exportpage.css';

function Exportpage() {
    const location = useLocation();
    const [mergedAudio, setMergedAudio] = useState(null);
    const waveformRef = useRef(null);
    const timelineRef = useRef(null);
    const wavesurfer = useRef(null);

    // Initialize WaveSurfer when the audio is ready
    useEffect(() => {
        if (mergedAudio && waveformRef.current) {
            wavesurfer.current = WaveSurfer.create({
                container: waveformRef.current,
                waveColor: '#000',
                progressColor: '#ff6600', // Color of the progress wave
                height: 80, // Reduce height for a shorter waveform
                barWidth: 2,
                barGap: 1, // Add gaps between bars for more waves
                cursorColor: '#ff6600', // Color of the cursor
                normalize: true, // Normalize the waveform to add variation
                responsive: true,
            });

            // Add the timeline plugin for a straight line underneath the waveform
            wavesurfer.current.registerPlugin(
                TimelinePlugin.create({
                    container: timelineRef.current,
                    height: 75, // Height for the timeline
                    timeInterval: 0.5, // Time intervals
                    primaryLabelInterval: 1,
                    secondaryLabelInterval: 0.5,
                    primaryColor: '#000',
                    secondaryColor: '#999',
                    primaryFontColor: '#000',
                    secondaryFontColor: '#999',
                })
            );

            wavesurfer.current.load(mergedAudio); // Load the merged audio
        }
    }, [mergedAudio]);

    // Ensure mergedAudio is set when location.state changes
    useEffect(() => {
        if (location.state && location.state.mergedAudio) {
            setMergedAudio(location.state.mergedAudio);
        }
    }, [location.state]);

    const playPause = () => {
        if (wavesurfer.current) {
            if (wavesurfer.current.isPlaying()) {
                wavesurfer.current.pause();
            } else {
                wavesurfer.current.play();
            }
        }
    };

    return (
        <div className="pagebackground">
            {/* Circle-shaped image */}
            <div className="circle-shape"></div>

            {/* Waveform container */}
            <div id="waveform" ref={waveformRef} className="waveform"></div>

            {/* Timeline container */}
            <div id="timeline" ref={timelineRef} className="timeline"></div>

            {/* Audio controls */}
            <div className="controls">
                <button onClick={playPause} className="play-button">
                    Play/Pause
                </button>
            </div>
        </div>
    );
}

export default Exportpage;
