import React, { useEffect, useRef } from 'react';
import WaveSurfer from 'wavesurfer.js';

function Landingpage({ audioFiles }) {
    const waveformRef = useRef(null);

    useEffect(() => {
        if (audioFiles && audioFiles.length > 0) {
            const wavesurfer = WaveSurfer.create({
                container: waveformRef.current,
                waveColor: 'blue',
                progressColor: 'purple',
                height: 100,
            });

            wavesurfer.load(audioFiles[0].url);  // Load the first audio file for example
        }
    }, [audioFiles]);

    return (
        <div>
            <h1>Your Selected Audio Tracks</h1>
            <div ref={waveformRef}></div>  {/* Waveform will be displayed here */}
            {audioFiles && audioFiles.length > 0 && (
                <audio controls>
                    <source src={audioFiles[0].url} type={audioFiles[0].mimeType} />
                    Your browser does not support the audio element.
                </audio>
            )}
        </div>
    );
}

export default Landingpage;
