import menubutton from '../images/menubutton.png';
import React, { useState, useEffect } from "react";
import { Client } from "@gradio/client";


// CSS styling for the component
const menubackground = {
  width: '25%',
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'flex-start',
  alignItems: 'left',
  border: '2px solid #ffffff',
  backgroundColor: '#ffffff',
  fontFamily: "'Concert One', cursive",
  fontSize: '1.2rem',
  boxShadow: '0px 0px 15px 5px rgba(255, 255, 255, 0.6)',
  border: 'none',
  position: 'fixed',
  top: '0',
  left: '0',
  zIndex: '1000',
  paddingTop: '50px',
};

const backbuttonStyle = {
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'center',
  position: 'fixed',
  top: '1%',
  left: '0.5%',
  zIndex: '1001',
  cursor: 'pointer',
  backgroundColor: 'rgba(255, 255, 255, 0.0)',
  border: 'none',
  padding: '0',
  width: 'auto',
  height: 'auto',
};

const textstyle = {
  color: '#000000',
  fontSize: '2rem',
  textAlign: 'center',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  marginTop: '50px',
};

const backButtonStyle = {
  marginTop: '10px',
  background: '#007bff',
  color: '#fff',
  border: 'none',
  padding: '10px 20px',
  borderRadius: '5px',
  cursor: 'pointer',
  fontSize: '16px',
  textAlign: 'center',
  width: 'auto',
  display: 'inline-block',
  transition: 'background 0.3s ease',
};

const contentStyle = {
  flexGrow: 1,
  paddingTop: '60px',
};

const generateButtonStyle = {
  marginTop: '20px',
  background: '#28a745',
  color: '#fff',
  border: 'none',
  padding: '10px 20px',
  borderRadius: '5px',
  cursor: 'pointer',
  fontSize: '16px',
  textAlign: 'center',
  width: 'auto',
  display: 'inline-block',
  transition: 'background 0.3s ease',
};

const descriptionBoxStyle = {
  marginTop: '20px',
  padding: '10px',
  borderRadius: '5px',
  border: '1px solid #ccc',
  width: '90%',
};

//kevin added
const fs = require('fs');
const axios = require('axios');

// Helper function to pause execution for a specified amount of time
const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms));
//kevin added above to try to connect to API like testApi.js

function AIMenu({ handleBack }) {
  const [loading, setLoading] = useState(false);
  const [description, setDescription] = useState("");
  const [app, setApp] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);

  // Initialize the Gradio client and store it in state
  useEffect(() => {
    const connectToClient = async () => {
      try {
        console.log("Connecting to the MusicGen API...");
        const appInstance = await GradioClient("https://facebook-musicgen.hf.space/");
        console.log("Connected to the API.");
        setApp(appInstance);
      } catch (error) {
        console.error("Failed to connect to the API:", error);
        setErrorMessage("Failed to connect to the MusicGen API. Please try again later.");
      }
    };

    connectToClient();
  }, []);

  const handleGenerateMusic = async () => {
    if (!app) {
      alert('App is still connecting, please wait.');
      return;
    }

    setLoading(true);
    try {
      console.log("Generating music for description:", description);
      const result = await app.predict(0, [description]);
      console.log("Generated Music Result:", result?.data);

      const wavFilePath = result?.data?.[1]?.name;
      if (!wavFilePath) {
        throw new Error("No WAV file found in the result.");
      }

      const wavFileUrl = `https://facebook-musicgen.hf.space/file=${wavFilePath}`;
      console.log("WAV file URL:", wavFileUrl);

      await downloadFile(wavFileUrl, description);

    } catch (error) {
      console.error("Error generating or downloading music:", error);
      alert(`Failed to generate or download music: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const downloadFile = async (fileUrl, description) => {
    try {
      const response = await fetch(fileUrl);
      if (!response.ok) {
        throw new Error("Failed to download the music file.");
      }

      const blob = await response.blob();
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `${description}_generated_music.wav`;
      document.body.appendChild(a);
      a.click();
      a.remove();
      URL.revokeObjectURL(url);
    } catch (error) {
      console.error("Error downloading the file:", error);
      alert(`Error downloading the file: ${error.message}`);
    }
  };

  if (errorMessage) {
    return <div>{errorMessage}</div>;
  }
  return (
    <div style={menubackground}>
      <div style={contentStyle}>
        <h1>This Is The AI Menu</h1>
        {/* Textbox for user to input description */}
        <textarea
          style={descriptionBoxStyle}
          placeholder="Describe your music..."
          value={description}
          onChange={(e) => setDescription(e.target.value)} // Update state on change
        />
        {/* Button to generate music */}
        <button style={generateButtonStyle} onClick={handleGenerateMusic} disabled={loading}>
          {loading ? 'Generating...' : 'Generate Music'}
        </button>
        {/* If an audio URL is available, provide a way to play it */}
        {audioUrl && (
          <audio controls>
            <source src={audioUrl} type="audio/wav" />
            Your browser does not support the audio tag.
          </audio>
        )}
        {/* Back button to close the menu */}
        <button style={backButtonStyle} onClick={handleBack}>Back</button>
      </div>
    </div>
  );
}

export default AIMenu;
