import React, { useState } from 'react';
import menubutton from '../images/menubutton.png';
import sparkless from '../images/drawstar.png';
import Menu from './Menu.js';
import AIMenu from './Ai.js';

const sidebarContainerStyle = {
    width: '25%',
    height: '100%',
    display: 'flex',
    justifyContent: 'left',
    alignItems: 'center', 
    flexDirection: 'column',
    border: '2px solid #ffffff',
    backgroundColor: 'rgba(255, 255, 255, 0.3)',
    fontFamily: "'Concert One', cursive",
    textAlign: 'left',
    fontSize: '1.2rem',
    boxShadow: '0px 0px 15px 5px rgba(255, 255, 255, 0.6)',
    position: 'fixed',
    top: '0',
    left: '0',
    zIndex: '1001',
    paddingTop: '5%',
};

const buttonStyle = {
    padding: '8px 16px',
    borderRadius: '30px',
    border: '2px solid #ffffff',
    backgroundColor: 'rgba(255, 255, 255, 0.5)',
    color: '#000',
    fontFamily: "'Concert One', cursive",
    cursor: 'pointer',
    width: '90%',
    marginBottom: '10px',
    textAlign: 'center',
    fontSize: '1rem',
    boxShadow: '2px 2px 4px white, -2px -2px 4px white',
    alignSelf: 'center',
};

const menubuttonStyle = {
    display: 'flex',
    flexDirection: 'row',  
    alignItems: 'center',
    justifyContent: 'center',
    position: 'fixed',
    top: '1%',
    left: '0.5%',
    zIndex: '1001',
    cursor: 'pointer',
    backgroundColor: 'rgba(255, 255, 255, 0.0)',
    border: 'none',
    padding: '0',
    width: 'auto',
    height: 'auto',
};

const headingStyle = {
    display: 'flex',
    flexDirection: 'row',  
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: '1.5rem',
    color: '#000000',
    margin: 0,
    padding: 0,
    fontFamily: 'Concert One',
    marginLeft: '10px',
    marginTop: '-2px',
    height: 'auto',
};

function Sidebar() {
  const [isMenuVisible, setMenuVisible] = useState(null);

  const toggleMenu = (menu) => {
    setMenuVisible(isMenuVisible === menu ? null : menu);
  };

  const handleBack = () => {
    setMenuVisible(null); // Hide the current menu
  };

  return (
    <div style={sidebarContainerStyle}>
      <button style={menubuttonStyle} onClick={() => toggleMenu('mainMenu')}>
        <img src={menubutton} alt="Menu Button" style={{ width: '33px', height: 'auto'}} />
        <h1 style={headingStyle}>MELLOWDIES</h1>
      </button>
      <button style={buttonStyle} onClick={() => toggleMenu('aiSuggestionMenu')}>
        <h1 style={headingStyle}>✨AI Suggestion✨</h1>
      </button>
      <button style={buttonStyle} onClick={() => toggleMenu('mixerMenu')}>
        <h1 style={headingStyle}>Mixer</h1>
      </button>

      {/* Conditionally render different menus based on which one is visible */}
      {isMenuVisible === 'mainMenu' && <Menu handleBack={handleBack} />}
      {isMenuVisible === 'aiSuggestionMenu' && <AIMenu handleBack={handleBack} />}
      {isMenuVisible === 'mixerMenu' && (
        <Menu
          handleBack={handleBack}
          handleVolumeChange={(volume) => { /* Volume handling logic */ }}
          handlePlaybackRateChange={(rate) => { /* Playback rate handling logic */ }}
          handleEffects={(effectType, value) => { /* Effects handling logic */ }}
        />
      )}
    </div>
  );
}

export default Sidebar;
