import React, { useEffect, useRef, useState } from 'react';
import WaveSurfer from 'wavesurfer.js';
import { useLocation } from 'react-router-dom';
import Sidebar from './Sidebar.js';
import cloud from '../images/cloud.png';
import PlayButton from './PlayButton.js';

const pagebackground = {
    backgroundSize: 'cover',
    backgroundImage: `url(${cloud})`,
    backgroundPosition: 'center',
    padding: '10px',
    height: '100vh',
    margin: 0,
    display: 'flex',
    flexDirection: 'column',
    fontFamily: 'Concert One',
};

const musicbackground = {
    width: '75%',
    height: '80%',
    position: 'fixed',
    top: '0',
    right: '0',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'left',
    backgroundColor: 'rgba(255, 255, 255, 0.3)',
    marginTop: '5px',
    boxShadow: '0px 0px 15px 5px rgba(255, 255, 255, 0.6)',
    overflowY: 'auto',
    padding: '10px',
};

const trackNameStyle = {
    position: 'relative',
    color: '#000000',
    fontSize: '0.8rem',
    zIndex: '1002',
    padding: '5px',
    borderRadius: '5px',
    backgroundColor: 'rgba(255, 255, 255, 0.7)',
};

const waveformStyle = {
    width: '100%',
    height: '75px',
    position: 'relative',
    marginBottom: '20px',
};

function Landingpage() {
    const wavesurferRefs = useRef([]);
    const location = useLocation();
    const { audioFiles } = location.state || { audioFiles: [] };
    const [isReady, setIsReady] = useState(false);
    const [speed, setSpeed] = useState(1);
    const [progress, setProgress] = useState(0);

    useEffect(() => {
        wavesurferRefs.current = [];
        if (audioFiles && audioFiles.length > 0) {
            audioFiles.forEach((file, index) => {
                const waveSurfer = WaveSurfer.create({
                    container: `#waveform-${index}`,
                    waveColor: 'blue',
                    progressColor: '#00FFFF',
                    height: 75,
                    backend: 'MediaElement',
                    plugins: [
                        RegionsPlugin.create(),
                    ],
                });

                waveSurfer.load(file.url);

                waveSurfer.on('ready', () => setIsReady(true));

                waveSurfer.on('audioprocess', () => {
                    setProgress(waveSurfer.getCurrentTime() / waveSurfer.getDuration() * 100);
                });

                waveSurfer.on('seek', (progress) => {
                    setProgress(progress * 100);
                });

        

                wavesurferRefs.current[index] = waveSurfer;
            });

            return () => {
                wavesurferRefs.current.forEach(waveSurfer => waveSurfer.destroy());
            };
        }
    }, [audioFiles]);

    const handlePlaybackRateChange = (rate) => {
        setSpeed(rate);
        wavesurferRefs.current.forEach(waveSurfer => waveSurfer.setPlaybackRate(rate));
    };

    const Trim = {
        loop: EL_loop.checked,
        options: {
          id: "Trim",
          start: 0,
          color: "rgba(0, 100, 255, 0.1)",
        },
        time: 0,
        create(re) {
          this.options.start = 0; // Set Trim start
          this.options.end = wavesurfer.getDuration(); // Set Trim end to match audio duration
          wavesurfer.addRegion(this.options); // Add Trim region to WaveSurfer instance
          this.Region = wavesurfer.regions.list[this.options.id];
        },
        trimTime() {
          const wasPlaying = wavesurfer.isPlaying();
          const re = this.Region;
          const isEnd = this.time >= re.end;
          this.time = Math.min(Math.max(wavesurfer.getCurrentTime(), re.start), re.end);
          if (isEnd) this.time = re.start;
          if (isEnd && !this.loop) wavesurfer.pause();
          wavesurfer.setCurrentTime(Math.max(0, this.time)); // https://github.com/katspaugh/wavesurfer.js/issues/1816
        },
        play() {
          wavesurfer.playPause();
        },
      };
      
      const updateUI = () => {
        EL_play.classList.toggle("isPlaying", wavesurfer.isPlaying());
      };
      
      EL_loop.addEventListener("change", () => Trim.loop = EL_loop.checked); // play pause events
      EL_play.addEventListener("click", () => Trim.play()); // play pause events
      wavesurfer.on("region-updated", () => Trim.trimTime());
      wavesurfer.on("ready", () => Trim.create());
      wavesurfer.on("play", updateUI);
      wavesurfer.on("pause", updateUI);
      wavesurfer.on("audioprocess", () => Trim.trimTime());
      wavesurfer.load("http://upload.wikimedia.org/wikipedia/en/4/45/ACDC_-_Back_In_Black-sample.ogg"); // Load Sound!

    return (
        <div style={pagebackground}>
            <div style={musicbackground}>
                {audioFiles.map((file, index) => (
                    <div key={index} style={waveformStyle}>
                        <div style={trackNameStyle}>{file.name}</div>
                        <div id={`waveform-${index}`} style={{ width: '100%', height: '100%' }}></div>
                    </div>
                ))}
            </div>

            <Sidebar />

            {audioFiles && audioFiles.length > 0 ? (
                <PlayButton
                    playAllTracks={() => wavesurferRefs.current.forEach(waveSurfer => waveSurfer.playPause())}
                    forwardAllTracks={() => {
                        wavesurferRefs.current.forEach(waveSurfer => {
                            const currentTime = waveSurfer.getCurrentTime();
                            const duration = waveSurfer.getDuration();
                            waveSurfer.seekTo((currentTime + 5) / duration);
                        });
                    }}
                    backwardAllTracks={() => {
                        wavesurferRefs.current.forEach(waveSurfer => {
                            const currentTime = waveSurfer.getCurrentTime();
                            waveSurfer.seekTo(Math.max((currentTime - 5) / waveSurfer.getDuration(), 0));
                        });
                    }}
                    seekAllTracks={(seekTo) => wavesurferRefs.current.forEach(waveSurfer => waveSurfer.seekTo(seekTo))}
                    changeSpeedAllTracks={handlePlaybackRateChange}
                    isReady={isReady}
                    speed={speed}
                />
            ) : (
                <p>No audio tracks available.</p>
            )}
        </div>
    );
}

export default Landingpage;
